---
import '@fontsource-variable/inter'

import Header from "../components/Header/Header.astro";
import Footer from '../components/Footer/Footer.astro';

const { title, published, tags } = Astro.props.content;
---

<style is:global>
  .markdown-content {
    @apply leading-8;
  }

  .markdown-content h1 {
    @apply text-4xl font-bold mt-12 mb-6;
  }

  .markdown-content h2 {
    @apply text-3xl font-bold mt-12 mb-6;
  }

  .markdown-content h3 {
    @apply text-2xl font-bold mt-12 mb-6;
  }

  .markdown-content h4 {
   @apply text-lg text-tertiary-200 underline font-semibold my-4; 
  }

  .markdown-content ol {
    @apply list-decimal my-4 pl-10;
  }


  .markdown-content ul {
    @apply list-disc my-4 pl-10;
  }

  .markdown-content hr {
    @apply border-gray-600 my-6;
  }

  .markdown-content p {
    @apply text-base leading-8 text-tertiary-100;
  }
  .markdown-content img {
    @apply my-6;
  }

  .markdown-content blockquote {
    @apply p-4 my-6 border-s-4 border-gray-300 bg-secondary-100;
  }

  .markdown-content blockquote p {
    @apply text-lg font-medium italic;
  }

  .markdown-content pre {
    @apply px-4 py-2 my-4 rounded-sm shadow-sm;
  }

  .markdown-content table {
    /* @apply table table-fixed my-8; */
    @apply w-full text-sm text-left rtl:text-right text-tertiary-200 my-6;
  }

  .markdown-content table thead {
    @apply text-sm text-tertiary-200 uppercase bg-secondary-200 ;
  }

  .markdown-content table thead th {
    @apply px-6 py-3;
  }

  .markdown-content table tbody tr {
    @apply odd:bg-secondary-100 even:bg-secondary-200 border-b border-gray-700;
  }

  .markdown-content table tbody tr td {
    @apply px-6 py-4;
  }

  .markdown-content a {
    @apply leading-loose text-blue-600 border-b pb-1 border-blue-600 border-dashed;
  }

  .markdown-content code {
    @apply font-mono leading-loose text-sm bg-[#24292e] px-2 py-1 rounded-sm text-red-600;
  }
</style>

<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1">
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <title>{title}</title>
  </head>
  <body class="bg-body w-full h-full bg-center bg-no-repeat bg-cover">
    <Header />
    <article class="flex flex-col pt-40 w-[84%] mx-auto xl:container xl:justify-between xl:w-[60%]">
      <div class="flex flex-col gap-4">
        <h1 class="text-tertiary-100 text-4xl md:text-5xl font-bold">{title}</h1>
        <div class="flex flex-row gap-6">
          <div class="flex flex-row text-sm text-tertiary-100 gap-2">
            <p>Published on</p>
            <strong>{published}</strong>
          </div>
          <div class="flex flex-row gap-1">           
            {tags.map(tag => <p class="text-sm text-blue-400 underline">{tag}</p>)}            
          </div>
        </div>
      </div>
      <div class="text-tertiary-100 py-10 markdown-content">
        <slot></slot>
      </div>
    </article>
    <Footer />
  </body>
</html>

